const background ="https://www.travelplusstyle.com/wp-content/uploads/2016/01/sonevajani-1880.jpg";
const style = theme => ({
    mainheader:{
        height:"84vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
      },
      overlay: {
        position: 'absolute',
        top: 0,
        bottom: 0,
        right: 0,
        left: 0,
        height:"100%",
        backgroundColor: 'rgba(0,0,0,.3)'
      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"90vh",
      fontFamily:"Roboto",
      flexDirection:"column"
    },
    button:{
        marginLeft:10,
        padding:"5px 50px",
    },
  typo:{

      padding:20,
      fontFamily:"Roboto",
      fontSize:"4rem",
      fontWeight:"500"
    },
    Search:{
      background:"white",
      height:"15%",
      width:"100%",
    },
    textField:{
        marginTop:"2rem"
    }


});

export default style;
