const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
            },
        overlay:{
          height:"100vh",
        postion:"absolute",
        top:"0",
        width:"100%",
        left:"0",
        backgroundColor: 'rgba(255, 255, 255, 0.)',
        },
    typo:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"100vh",
      fontFamily:"Roboto"
    }

});

export default style;
