const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
        [theme.breakpoints.up('xs')]: {
          height: '60vh',
          minHeight: 400,
          maxHeight: 500,
        },

      },
    // typo:{
    //   display:"flex",
    //   alignItems:"Center",
    //   justifyContent:'Center',
    //   color:"white",
    //   height:"100vh",
    //   fontFamily:"Roboto"
    // }

});

export default style;
