import { fade } from '@material-ui/core/styles/colorManipulator';
const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
        backgroundColor:"red",
            },
            overlay:{
                backgroundColor:"red",
                position:"absolu"
            },
    typo:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"100vh"
    }

});

export default style;
