const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
        [theme.breakpoints.up('sm')]: {
          height: '90vh',
          minHeight: 500,
          maxHeight: 1300,
        },

      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"100vh",
      fontFamily:"Roboto",
      flexdirection:"column"
    },


});

export default style;
