const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",


      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"90vh",
      fontFamily:"Roboto",
      flexDirection:"column"
    },
    button:{
        marginLeft:10,
        padding:"5px 50px",
    },
    typo:{
      color:"white",
      padding:20,
      fontFamily:"Roboto",
      fontSize:"4rem",
      fontWeight:"500"
    },
    Search:{
      background:"#C51162",
      height:"15%",
      width:"100%",
    }


});

export default style;
