const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover cover"
    },
    overlay:{
      position:"absolute",
      top:"0",

      left:"0",
      height:"100vh",
      width:"100%",



    }


});

export default style;
