const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover"
    },
    overlay:{
      position:"absolute",
      top:"0",
      backgroundColor:"rgba(0,0,0,0.7)",
      left:"",
      height:"90vh",
      width:"100%",



    }


});

export default style;
