const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",


      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"90vh",
      fontFamily:"Roboto",
      flexDirection:"column"
    },
    button:{
        // border:"1px solid #1CC3B2",
        marginLeft:10,
        padding:"5px 50px",
        [theme.breakpoints.up('xs')]: {
         margin:"2px 150px"
        },
    },
    typo:{
      color:"white",
      padding:20,

    }


});

export default style;
