const background ="https://www.travelplusstyle.com/wp-content/uploads/2016/01/sonevajani-1880.jpg";
// import useMediaQuery from '@material-ui/core/useMediaQuery';
const style = theme => ({
    mainheader:{
        height:"95vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
      },
      overlay: {
        position: 'absolute',
        top: 0,
        bottom: 0,
        right: 0,
        left: 0,
        height:"100%",
        backgroundColor: 'rgba(0,0,0,.3)'
      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"80vh",
      fontFamily:"Roboto",
      flexDirection:"column"

    },
    button:{
        marginLeft:10,
        padding:"5px 50px",
    },
  typo:{
      padding:30,
      color:"white",
      zIndex:9,
      backgroundColor:"rgba(0,0,0,0.2)",
      fontFamily:"Roboto",
      fontSize:theme.spacing(6),
      fontWeight:500,
    },
    Search:{
      height:100,
      width:"100%",
    },
    textField:{
      display:"flex",
      position:"relative",
      top:theme.spacing(4),
      border:"none",
      borderRadius:"1%",
      backgroundColor:"white",
      alignItems:"center",
    },
    cardGrid: {
      paddingTop: theme.spacing(8),
      paddingBottom: theme.spacing(0)
    },
    card: {
      height: '100%',
      display: 'flex',
      flexDirection: 'column'
    },
    cardMedia: {
      paddingTop: '56.25%'
    },
    cardContent: {
      flexGrow: 1
    }
  });
export default style;
