const background ="/images/Header.jpg";
const style = theme => ({
    mainheader:{
        height:"90vh",
        position:"relative",
        background:`url(${background})`,
        backgroundPosition: 'center',
        backgroundSize:"cover",
        [theme.breakpoints.up('sm')]: {
          height: '90vh',
          minHeight: 500,
          maxHeight: 1300,
        },

      },
      headerContent:{
      display:"flex",
      alignItems:"Center",
      justifyContent:'Center',
      color:"white",
      height:"90vh",
      fontFamily:"Roboto",
      flexDirection:"column"
    },
    button:{
        border:"1px solid #1CC3B2",
        background:"none",
        marginLeft:10,
        padding:6
    }


});

export default style;
